import React from 'react';
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import cellEditFactory from 'react-bootstrap-table2-editor';
import './App.css';
import axios from 'axios';
import './Input.js';




const products = [ {"productID": 1,"productName": "Pięty Mojżesza", "productPrice": 234 },
 {"productID": 2,"productName": "Mokasyny Zagłady", "productPrice": 63 },
 {"productID": 3,"productName": "Sandały Asteriksa", "productPrice": 512 },
 {"productID": 4,"productName": "Croksy Dudy", "productPrice": 3 },
 {"productID": 5,"productName": "Buty Kota w Butach", "productPrice": 100 },];

//let products = [];
addProducts(20);


 
// Make a request for a user with a given ID
//axios.get('https://localhost:44303/api/getProducts')
//  .then(function (response) {
//    // handle success
 ///   console.log(response);
//    products=response.data;
//  });

  axios.post(`https://localhost:44303/api/getProducts`, { products })
  .then(res => {
    console.log(res);
    console.log(res.data);
  });


  
const columns = [{
  dataField: 'productID',
  text: 'ID Produktu',
  sort: true
}, {
  dataField: 'productName',
  text: 'Nazwa Produktu',
  sort: true
}, {
  dataField: 'productPrice',
  text: 'Cena Produktu',
  sort: true
}];

const defaultSorted = [{
  dataField: 'id',
  order: 'asc'
}];

 function addProducts(quantity) {
   const startId = products.length;
   for (let i = 0; i < quantity; i++) {
    const id = startId + i;
    products.push({
      productID: id,
      productName: 'Item name ' + id,
      productPrice: 2100 + i
     });
   }
 }



const CaptionElement = () => <h3 style={{ borderRadius: '0.25em', textAlign: 'center', color: 'black', border: '1px solid black', padding: '0.5em' }}>Produkty ale w przyszłości przychody</h3>;


export default () =>
<div ><BootstrapTable bootstrap4 caption={<CaptionElement />} keyField='id' data={products} columns={ columns } defaultSorted={ defaultSorted }/><BootstrapTable
  keyField="id"
  data={ products }
  columns={ columns }
  cellEdit={ cellEditFactory({ mode: 'click' }) }
/>
</div>
