import React, { useState, useEffect } from "react";
import Axios from "axios";
import BootstrapTable from 'react-bootstrap-table-next';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
//import React from "react";
//import ReactDOM from "react-dom";
import { useFormik } from "formik";
import "./styles.css";

//let products = [];

const columns = [{
  
    dataField: 'productID',
    text: 'ID Produktu',
    sort: true
  }, {
    dataField: 'productName',
    text: 'Nazwa Produktu',
    sort: true
  }, {
    dataField: 'productPrice',
    text: 'Cena Produktu',
    sort: true
  }];

  //function addProducts(quantity) {
  //  const startId = products.length;
  //  for (let i = 0; i < quantity; i++) {
  //  const id = startId + i;
  //   products.push({
  //    productID: id,
  //    productName: 'Item name ' + id,
  //    productPrice: 2100 + i
  //   });

  // }
  // return products
  //}

export default function Fetcher({ url }) {
  const [data, setData] = useState([null]);
   
  const lightOn = () =>
  setData(data =>  [...data, {productID: 2,
    productName: 'Item name ' + 1,
    productPrice: 2100 + 4} ])



  const SignupForm = () => {
    const formik = useFormik({
      initialValues: { productID: "", productName: "", productPrice:"" },
     onSubmit: values = () =>
     setData(data =>  [...data, {productID: 2,
       productName: 'Item name ' + 1,
       productPrice: 2100 + 4} ])
     
     
     // onSubmit: values => {
      //  alert(JSON.stringify(values, null, 2));
     // }
    });
    return (
      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="productID">productID</label>
        <input
          id="productID"
          name="productID"
          type="number"
          placeholder="ID produktu"
          onChange={formik.handleChange}
          value={formik.values.email}
        />
         <label htmlFor="productName">productName</label>
        <input
          id="productName"
          name="productName"
          type="text"
          placeholder="Nazwa produktu"
          onChange={formik.handleChange}
          value={formik.values.email}
        />
         <label htmlFor="productPrice">productPrice</label>
        <input
        id="productPrice"
          name="productPrice"
          type="number"
          placeholder="Cena produktu"
          onChange={formik.handleChange}
          value={formik.values.email}
        />
        <button type="submit">Submit</button>
      </form>
    );
  };
  
  
  
  
  
  
  
  
  
  
  //data.push({ //productID: 2,
        //productName: 'Item name ' + 1,
        //productPrice: 2100 + 4
    //   });

       
  useEffect(
    () => {
      // Start it off by assuming the component is still mounted
      let mounted = true;

      const loadData = async () => {
        const response = await Axios.get('https://localhost:44351/api/Products');
        // We have a response, but let's first check if component is still mounted
        if (mounted) {
          setData(response.data);
        }
      };
      loadData();

      return () => {
        // When cleanup is called, toggle the mounted variable to false
        mounted = false;
      };
    },
    [url]
  );

  if (!data) {
    return <div>Loading data from {url}</div>;
  }

  return <div class="row justify-content-center"><BootstrapTable class="table table-bordered table-striped text-center" bootstrap4   keyField='id' data={data} columns={ columns } />
 <button onClick={lightOn}>On</button> <SignupForm/>
  </div>;
}
